export const client = createClient();

const redis = async () => {
  try {
    await client.connect();
    console.log('Redis client is Connected Successfully.....!!!!');
  } catch (error) {
    console.log('Error occured in Connecting Redis client...!!!!', error);
  }
};

export default redis;

