      channel.assertQueue(queue, {
        durable: false
      });
      channel.sendToQueue(queue, Buffer.from(msg));

      console.log(`Message📮 :`, msg);
    });
    setTimeout(function () {
      connection.close();
      process.exit(0);
    }, 500);
  });
};

